import { useState } from "react";

function BookingForm(){
    const [date, setDate] = useState(""); 
    const [time, setTime] = useState(""); 
    const [guests, setGuests] = useState("");
    const [occasion,setOccasion] = useState("");

    function clearForm(){
        setDate("");
        setTime("");
        setGuests("");
        setOccasion("");
    }

    function handleSubmit(e){
        console.log(date,time,guests,occasion);
        e.preventDefault(); 
        alert("Account created!"); 
        clearForm();
    }

    return(
        <form>
            <label htmlFor="res-date">Choose date</label>
            <input type="date" id="res-date" value={date} onChange={(e) => { 
               setDate(e.target.value); 
             }}></input>

            <label htmlFor="res-time">Choose time</label>
            <select id="res-time"  onChange={(e) => { 
               setTime(e.target.value); 
             }}>
                <option>17:00</option>
                <option>18:00</option>
                <option>19:00</option>
                <option>20:00</option>
                <option>21:00</option>
                <option>22:00</option>
            </select>

            <label htmlFor="guests">Number of guests</label>
            <input type="number" placeholder="1" min="1" max="10" id="guests" onChange={(e) => { 
               setGuests(e.target.value); 
             }}></input>

            <label htmlFor="occasion">Occasion</label>
            <select id="occasion" onChange={(e) => { 
               setOccasion(e.target.value); 
             }}>
                <option>Birthday</option>
                <option>Anniversary</option>
            </select>

            <input className="btn" type="submit" value="Make Your reservation" onClick={handleSubmit}></input>
        </form>
    ) 
}

export default BookingForm;
